import 'package:flutter/material.dart';import 'package:flutter_mobx/flutter_mobx.dart';import 'package:learning_get_it/models/post.dart';import 'package:learning_get_it/services/getit_service.dart';import 'detail_store.dart';class DetailPage extends StatelessWidget {   const DetailPage({Key? key}): super(key: key);  @override  Widget build(BuildContext context) {    DetailStore state = locator<DetailStore>();    return Scaffold(      appBar: AppBar(        centerTitle: true,        title: const Text("Detail Page"),        actions: [          TextButton(            onPressed: () {              print('ssss');              state.submit(context);            },            child:  Text(              state.post == null?"Creat":"Edit",              style: const TextStyle(                  color: Colors.white, fontSize: 19),            ),          ),        ],      ),      body: Observer(builder: (context){        return Stack(          children: [            Padding(              padding: const EdgeInsets.all(15.0),              child: Column(                children: [                  TextField(                    controller: state.titleController,                    style: const TextStyle(                      fontSize: 16,                      fontWeight: FontWeight.w600,                      color: Colors.black,                    ),                    decoration:                    const InputDecoration(hintText: "Title"),                  ),                  const SizedBox(                    height: 15,                  ),                  TextField(                    controller: state.bodyController,                    style: const TextStyle(                      fontSize: 16,                      fontWeight: FontWeight.w600,                      color: Colors.black,                    ),                    decoration:                    const InputDecoration(hintText: "Body"),                  ),                ],              ),            ),           Visibility(visible: state.isLoading,child:  Center(child:           CircularProgressIndicator(color:           Colors.green,),),)          ],        );      },),    );  }}